#include "appconfig.h"
#include <QJsonDocument>
#include <QFile>
#include <QJsonArray>
#include <QPixmap>
AppConfig::AppConfig()
{}

AppConfig::AppConfig(const QJsonObject &src) : QJsonObject(src)
{}

bool AppConfig::loadFromFile(const QString& fn) {
     QFile loadFile {fn};
     if(!loadFile.open(QIODevice::ReadOnly)) {
         qWarning("Cannot open file.");
         return false;
     }

     QByteArray saveData = loadFile.readAll();

     QJsonDocument loadDoc { QJsonDocument::fromJson(saveData) };

     *this = loadDoc.object();

     return true;
 }

 bool AppConfig::saveToFile(const QString& fn) {
     QFile saveFile {fn};
     if (!saveFile.open(QIODevice::WriteOnly)) {
         qWarning("Couldn't open save file.");
         return false;
     }

     QJsonDocument saveDoc(*this);
     saveFile.write( saveDoc.toJson() );

     return true;
 }

 AppConfig AppConfig::defaultConfig()
 {
     QJsonObject basic;
     basic["start_on_boot"] = true;
     basic["initial_mode"] = "standard"; // single_day, in_class
     basic["link_mode"] = "internet"; // local, offline
     basic["server_addr"] = "127.0.0.1";
     basic["server_interface"] = "http";
     basic["local_storage"] = "~/";

     QJsonObject user;
     user["avatar1"] = "";
     user["avatar2"] = "";
     user["avatar3"] = "";
     user["avatar4"] = "";

     user["province"] = "";
     user["city"] = "";
     user["district"] = "";
     user["school"] = "";
     user["class"] = "(15)";
     user["subject"] = "";

     QJsonObject schedule;
     schedule["sync_schl_sche"] = true;
     schedule["override_local"] = true;
     QJsonArray defcell;
     defcell.append("location");
     defcell.append("subject");
     defcell.append("class");
     QJsonArray defres;
     defres.append("bfcls");
     defres.append("incls");
     defres.append("aftcls");
     defres.append("raw");
     schedule["default_cell"] = defcell;
     schedule["default_resource"] = defres;
     schedule["read_schl_sche"] = true;
     schedule["tags"] = QJsonArray{ "", "", "" };

     QJsonObject rtn;
     rtn["basic"] = basic;
     rtn["user"] = user;
     rtn["schedule"] = schedule;

     return rtn;
 }

 AppConfig glbAppConfig;
